library("ggplot2")
qplot(log10(seq_along(granada.data$contributions)), log10(granada.data$contributions)) +     geom_point(aes(y=log10(almeria.data$contributions),x=log10(seq_along(almeria.data$contributions)),color='almeria.data$contributions')) +    geom_point(aes(y=log10(huelva.data$contributions), x=log10( seq_along(huelva.data$contributions)),color='huelva.data$contributions')) +    geom_point(aes(y=log10(cadiz.data$contributions), x=log10( seq_along(cadiz.data$contributions)),color='cadiz.data$contributions')) +    geom_point(aes(y=log10(malaga.data$contributions), x=log10( seq_along(malaga.data$contributions)),color='malaga.data$contributions')) +    geom_point(aes(y=log10(sevilla.data$contributions), x=log10( seq_along(sevilla.data$contributions)),color='sevilla.data$contributions')) +    geom_point(aes(y=log10(jaen.data$contributions), x=log10( seq_along(jaen.data$contributions)),color='jaen.data$contributions'))+    geom_point(aes(y=log10(cordoba.data$contributions), x=log10( seq_along(cordoba.data$contributions)),color='cordoba.data$contributions'))
ggsave("andalucia-zipf-github.png")
andalucia.data <- data.frame( city =c('almeria','cadiz','cordoba','granada','huelva','jaen','malaga', 'sevilla'), users=(c(length(almeria.data$login),length(cadiz.data$login),length(cordoba.data$login),length(granada.data$login),length(huelva.data$login),length(jaen.data$login),length(malaga.data$login),length(sevilla.data$login))), contrib=(c(sum(almeria.data$contributions),sum(cadiz.data$contributions),sum(cordoba.data$contributions),sum(granada.data$contributions),sum(huelva.data$contributions),sum(jaen.data$contributions),sum(malaga.data$contributions),sum(sevilla.data$contributions))), stars=(c(sum(almeria.data$stars),sum(cadiz.data$stars),sum(cordoba.data$stars),sum(granada.data$stars),sum(huelva.data$stars),sum(jaen.data$stars),sum(malaga.data$stars),sum(sevilla.data$stars))), followers=(c(sum(almeria.data$followers),sum(cadiz.data$followers),sum(cordoba.data$followers),sum(granada.data$followers),sum(huelva.data$followers),sum(jaen.data$followers),sum(malaga.data$followers),sum(sevilla.data$followers))))
